import React from 'react';
import "./settings-page.css";
import {BrowserRouter as Router, Route, Switch, Link, Redirect, useLocation} from "react-router-dom";
import AppHeader from '../appheaderpage/appheader';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'; 


function SettingsPage() {

  const data = useLocation().data;
  const username = data.username;
  const password = data.password;

    return (
      <div>
        <AppHeader username={username} password={password}/>
        <div className="settings-header">
          <header><h1>Settings</h1></header>
        </div>
        <Link to={{pathname:"/", data:{username,password}}}><button className="backhome">
          <FontAwesomeIcon icon = 'arrow-left' size = "4x"/>
        </button></Link>
        <div>
            <h3>How often do you want email notifications from assignment due date</h3>

            <button onClick={setNotification}>Never</button>
            <button onClick={setNotification}>1 hour before due</button>
            <button onClick={setNotification}>5 hours before due</button>
            <button onClick={setNotification}>1 day before due</button>
            <button onClick={setNotification}>All the above</button>

            <h3>How often do you want email notifications from assignment due date</h3>

            <button onClick={setNotification}>Never</button>
            <button onClick={setNotification}>1 hour before due</button>
            <button onClick={setNotification}>5 hours before due</button>
            <button onClick={setNotification}>1 day before due</button>
            <button onClick={setNotification}>All the above</button>

        </div>
      </div>
    );
  }
  
  export default SettingsPage;
